<?php
$number = str_split("");


$g = 0;
$index = 0;
$saveInd = 0;
while($index+12 <= 999){
$mult = 1;
	for($i = $index; $i <= $index + 12; $i++)//multiply 13 numbers
         {
		 if($number[$index]==0){break;}//if there is a 0 the 13 num* = 0
		 $mult *= $number[$i];
		 }
	if ($mult >= $g) //keep track of the multiplication values get the max
     {
     $g = $mult;
     $saveInd = $index;
	 }
$index++;
}

echo $g." max num and the digits are<br>";
for ($i = $saveInd; $i < $saveInd + 12; $i++){
   echo " ".$number[$i];
}
